@import url('https://fonts.googleapis.com/css?family=Raleway:300|Source+Code+Pro');

* {
  margin: 0;
  padding: 0;
  font-family: sans-serif;
}

header {
  color: white;
  border-bottom: 4px solid #222222;
  width: 100%;
  height: 80px;
  background: #363636;
  overflow: hidden;
}

header .navbar-band {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.navbar-band .logo {
  color: #f2f2f2;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: Raleway, sans-serif;
  background: #222222;
  height: 80px;
  text-transform: uppercase;
  font-size: 2em;
  padding: 0 20px;
}

.nav-toggle {
  margin: 0 20px 0 0;
}

.nav-toggle .line-1, .line-2, .line-3 {
  width: 25px;
  height: 3px;
  background-color: #f2f2f2;
  margin: 6px 0;
}

.nav-toggle .line-2 {
  transition: .3s ease;
}

.nav-toggle .line-1, .line-3 {
  transition: .3s ease;
}

.x .line-1 {
  -webkit-transform: rotate(-45deg) translate(-9px, 6px);
  transform: translate(0, 7px) rotate(-135deg);
  transition: .3s ease;
}

.x .line-2 {
  opacity: 0;
  transition: .1s ease;
}

.x .line-3 {
  -webkit-transform: rotate(45deg) translate(-9px, -6px);
  transform: translate(0, -7px) rotate(135deg);
  transition: .3s ease;
}

header .navigation {
  margin-left: 24px;
  width: 40%;
}

header .nav-links {
  display: flex;
  width: 100%;
  align-content: space-between;
  text-decoration: none;
}

header .nav-link {
  list-style: none;
  font-family: 'Source Code Pro', monospace, sans-serif;
}

.main-content {
  height: 800px;
  background: #f2f2f2;
  padding-top: 20px;
}

.hero-unit {
  display: flex;
  align-items: center;
  justify-content: center;
  background: #363636;
  width: 90%;
  padding: 20px 0;
  margin: 0 auto;
  border-radius: 10px;
  color: #f2f2f2;
  -webkit-box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
  box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
}

.hero-unit p, h1 {
  font-family: 'Source Code Pro', monospace, sans-serif;
}
