@import url('https://fonts.googleapis.com/css?family=Nunito|Raleway:300|Source+Code+Pro');

* {
  margin: 0;
  padding: 0;
  font-family: Nunito, sans-serif;
  color: #222222;
  -webkit-font-smoothing: antialiased;
}

body {
  background: #f2f2f2;
}

header {
  color: white;
  width: 100%;
  height: 80px;
  overflow: hidden;
  transition: all .3s ease;
}

header.toggled {
  height: 124px;
}

header .navbar-band {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #363636;
}

.navbar-band .logo {
  color: #f2f2f2;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: Raleway, sans-serif;
  background: #222222;
  height: 80px;
  text-transform: uppercase;
  font-size: 2em;
  padding: 0 20px;
}

/* logo links */
.social-links {
  width: 96px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.github-link, .linkedin-link {
  height: 32px;
}

svg.github, svg.linkedin {
  fill: #fff;
}


/* burger nav toggle */
.nav-toggle {
  margin: 0 20px 0 0;
}

.nav-toggle .line-1, .line-2, .line-3 {
  width: 25px;
  height: 3px;
  background-color: #f2f2f2;
  margin: 6px 0;
}

.nav-toggle .line-2 {
  transition: .3s ease;
}

.nav-toggle .line-1, .line-3 {
  transition: .3s ease;
}

.x .line-1 {
  -webkit-transform: rotate(-45deg) translate(-9px, 6px);
  transform: translate(0, 9px) rotate(-135deg);
  transition: .3s ease;
}

.x .line-2 {
  opacity: 0;
  transition: .1s ease;
}

.x .line-3 {
  -webkit-transform: rotate(45deg) translate(-9px, -6px);
  transform: translate(0, -9px) rotate(135deg);
  transition: .3s ease;
}

nav.navigation {
  padding: 12px 0;
  width: 100%;
  background: #222;
}

.navigation .nav-links {
  display: flex;
  width: 100%;
  justify-content: space-evenly;
}

a.nav-link {
  list-style: none;
  font-family: 'Source Code Pro', monospace, sans-serif;
  color: #fff;
  text-decoration: none;
  border-bottom: 1px solid transparent;
}

a.nav-link:hover {
  color: #fc3c63;
  border-bottom: 1px solid #fc3c63;
}

.main-content {
  padding-top: 20px;
  width: 95%;
  margin: 0 auto;
}

.hero-unit {
  display: flex;
  align-items: center;
  justify-content: center;
  background: #363636;
  padding: 20px 0;
  border-radius: 3px;
  -webkit-box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
  box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
}

.hero-unit p, h1 {
  font-family: 'Source Code Pro', monospace, sans-serif;
  color: #f2f2f2;
}

.intro-text {
  margin-top: 10px;
}

/* Project Example */
.project-deck {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}

.project-card {
  background: #fff;
  border-radius: 3px;
  padding: 10px;
  -webkit-box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  margin: 20px 0;
  width: 100%;
}

.project-card .card-thumb {
  width: 100%;
  height: 200px;
  background-position: center;
  background-size: cover;
  background-color: #222222;
  margin: 0 auto;
  border-radius: 3px;
  position: relative;
  top: -20px;
  -webkit-box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  transition: .4s ease;
}

.card-thumb:hover {
  top: -25px;
  -webkit-box-shadow: 0 10px 40px 0 rgba(62,57,107,.2);
  box-shadow: 0 6px 20px 0 rgba(62,57,107,.2);
}

.card-thumb.author-image {
  background-image: url('ae97ef59c99ea7424d125a829f3cbdf9.png');
}

.card-thumb.tic-tac-toe-image {
  background-image: url('70a1311cea91603762a8b4ef64927905.png');
}

.card-thumb.woap-image {
  background-image: url('fb188d7e0e8b2131c3a22068f81e798b.png')
}

a.code-button {

}

.project-card .card-tags {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  flex-wrap: wrap;
}

.card-tags p {
  color: #8f8f8f;
  margin-left: 10px;
  cursor: default;
}

.card-tags p:hover {
  color: #222;
}



@media screen and (min-width: 768px) {
  .project-card {
    width: 748px;
  }

  .main-content {
    width: 80%;
  }

  @media screen and (min-width:1100px) {
    .project-card {
      width: calc(50% - 2em);
    }
  }

}
