@import url('https://fonts.googleapis.com/css?family=Raleway:300|Source+Code+Pro');

* {
  margin: 0;
  padding: 0;
  font-family: sans-serif;
}

header {
  color: white;
  width: 100%;
  height: 80px;
  background: #363636;
  overflow: hidden;
}

header .navbar-band {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.navbar-band .logo {
  color: #f2f2f2;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: Raleway, sans-serif;
  background: #222222;
  height: 80px;
  text-transform: uppercase;
  font-size: 2em;
  width: 80px;
}

.navbar-band button.nav-toggle {
  width: 30px;
  height: 26px;

  background: transparent;
  border-top: 4px solid;
  border-bottom: 4px solid;

  color: #f2f2f2;
  font-size: 0;
  transition: all .25s ease-in-out;


}

.navbar-band button.nav-toggle:before, .navbar-band button.nav-toggle:after {
  content: '';
  width: 100%;
  height: 4px;
  position: absolute;
  top:50%;
  left:50%;
  background: currentColor;

  transform: translate(-50%, -50%);
  transition: transform .25s ease-in-out;
}

header .navigation {
  margin-left: 24px;
  width: 40%;
}

header .nav-links {
  display: flex;
  width: 100%;
  align-content: space-between;
  text-decoration: none;
}

header .nav-link {
  list-style: none;
  font-family: 'Source Code Pro', monospace, sans-serif;
}
