@import url('https://fonts.googleapis.com/css?family=Nunito|Raleway:300|Source+Code+Pro');

* {
  margin: 0;
  padding: 0;

}

body {
  background: #f2f2f2;
  color: #222222;
  font-family: Nunito, sans-serif;
  -webkit-font-smoothing: antialiased;
}

a {
  color: #222;
}

canvas {
  display: block;
  vertical-align: bottom;
}

#particles {
  position: absolute;
  width: 100%;
  height: calc(100vh - 80px);
  z-index: 0;
}

::selection {
  background: #222;
}

main {
  background: #f2f2f2;
}

header {
  width: 100%;
  height: 80px;
  overflow: hidden;
  transition: all .3s ease;
}

header.toggled {
  height: 124px;
}

header .navbar-band {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #363636;
}

.navbar-band .logo.typed-content {
  color: #f2f2f2;
  font-family: 'Source Code Pro';
  height: 51px;
  padding: 0 20px;
}

.navbar-band .logo.normal {
  color: #f2f2f2;
  font-family: 'Source Code Pro';
  padding: 0 20px;
  font-size: 1.4em;
  text-decoration: none;
}

.navbar-band a {
  text-decoration: none;
}

/* logo links */
.social-links {
  width: 72px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.github-link, .linkedin-link {
  height: 24px;
}

svg.github, svg.linkedin {
  fill: #fff;
}

/* burger nav toggle */
.nav-toggle {
  height: 80px;
  padding: 0 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: .2s;
}

.nav-toggle.x {
  background: #222222;
}

.nav-toggle .line-1, .line-2, .line-3 {
  width: 25px;
  height: 3px;
  background-color: #f2f2f2;
  margin: 6px 0;
}

.nav-toggle .line-2 {
  transition: .3s ease;
}

.nav-toggle .line-1, .line-3 {
  transition: .3s ease;
}

.x .line-1 {
  -webkit-transform: translate(0, 9px) rotate(-135deg);
  transform: translate(0, 9px) rotate(-135deg);
  transition: .3s ease;
}

.x .line-2 {
  opacity: 0;
  transition: .1s ease;
}

.x .line-3 {
  -webkit-transform: translate(0, -9px) rotate(135deg);
  transform: translate(0, -9px) rotate(135deg);
  transition: .3s ease;
}

nav.navigation {
  width: 100%;
  background: #222;
}

.navigation .nav-links {
  display: flex;
  width: 100%;
  justify-content: space-evenly;
}

a.nav-link {
  list-style: none;
  font-family: 'Source Code Pro', monospace, sans-serif;
  color: #8f8f8f;
  text-decoration: none;
  border-bottom: 2px solid transparent;
  padding: 12px 0 10px 0;
}

a.nav-link:hover, a.nav-link.active {
  color: #fff;
  border-bottom: 2px solid #fff;
}

.container {
  width: 95%;
  margin: 0 auto;
  z-index: 1;
}
.container h1 {
  font-family: 'Source Code Pro';
}

.hero-unit {
  min-height: calc(100vh - 80px);
  box-sizing: border-box;
  display: flex;
  align-items: center;

  background-image: linear-gradient(to bottom right, hsl(37, 100%, 95%), hsl(37, 100%, 80%), hsl(37, 100%, 75%), hsl(37, 100%, 70%), hsl(37, 100%, 60%));
  border-radius: 0 0 3px 3px;
  -webkit-box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
  box-shadow: 0 10px 40px 0 rgba(62,57,107,.2), 0 2px 9px 0 rgba(62,57,107,.2);
}


.intro-text {
  margin-top: 30px;
}

/* skills */
.skills {
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  padding-bottom: 20px;
}

.skill-container {
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: calc(50% - 2em)
}

.skill-container i {
  font-size: 3em;
}

.skill-container p {
  text-align: center;
  color: #595959;
}

/* Project Example */
.project-deck {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}

.project-card {
  background: #fff;
  border-radius: 3px;
  padding: 10px;
  -webkit-box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  margin: 20px 0;
  width: 100%;
}

.project-card .card-thumb {
  width: 100%;
  height: 200px;
  background-position: center;
  background-size: cover;
  background-color: #222222;
  margin: 0 auto;
  border-radius: 3px;
  position: relative;
  top: -20px;
  -webkit-box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  box-shadow: 0 2px 5px 0 rgba(88,88,102,.07);
  transition: .4s ease;
}

.card-thumb:hover {
  top: -25px;
  -webkit-box-shadow: 0 6px 20px 0 rgba(62,57,107,.2);
  box-shadow: 0 6px 20px 0 rgba(62,57,107,.2);
}

.card-thumb.author-image {
  background-image: url('ae97ef59c99ea7424d125a829f3cbdf9.png');
}

.card-thumb.tic-tac-toe-image {
  background-image: url('70a1311cea91603762a8b4ef64927905.png');
}

.card-thumb.woap-image {
  background-image: url('fb188d7e0e8b2131c3a22068f81e798b.png')
}

.project-card .card-tags {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  flex-wrap: wrap;
}

.card-tags p {
  color: #8f8f8f;
  margin-left: 10px;
  cursor: default;
}

.card-tags p:hover {
  color: #222;
}

a.see-all p {
  width: 100%;
  text-align: center;
  padding-bottom: 30px;
}

footer {
  width: 100%;
  height: 80px;
  color: white;
  background: #363636;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}



@media screen and (min-width: 768px) {
  .project-card {
    width: 748px;
  }

  .container {
    width: 70%;
  }

  .skill-container {
    margin: 30px 0;
    width: calc(33% - 2em)
  }

  a.see-all p {
    text-align: left;
  }

  @media screen and (min-width:1100px) {
    .project-card {
      width: calc(50% - 2em);
    }
  }

}
